mapper.update:
INSERT INTO tbl_like (column1, column2, ,like_type)
SELECT #{param1}, #{param2}
WHERE NOT EXISTS (
    SELECT 1 FROM tbl_like
    WHERE column1 = #{param1} AND column2 = #{param2}
);

DELETE FROM tbl_like
WHERE EXISTS (
    SELECT 1 FROM tbl_like
    WHERE column1 = #{param1} AND column2 = #{param2}
);
